class Solution:
    def repeatedStringMatch(self, a: str, b: str) -> int:
        if not set(b).issubset(set(a)):
            return -1

        A = a
        count = 1
        while len(A) < len(b):
            A += a
            count += 1
        if b in A:
            return count
        # this is actually tricky to figure out by plus one more a.. it has reached the end..
        if b in A+a:
            return count+1

        return -1


