def buildLPS(s):
    prefTable = [0]*(len(s)+1)
    prevLPS = 0
    i = 1
    while i < len(s):
        if s[i] == s[prevLPS]:
            prevLPS += 1
            i += 1
            prefTable[i] = prevLPS

        else:
            if prevLPS == 0:
                i += 1
            else:
                prevLPS = prefTable[prevLPS]

    return prefTable


class Solution:
    def repeatedStringMatch(self, a: str, b: str) -> int:
        lps = buildLPS(b)

        i = 0
        j = 0
        while i < len(a):
            while j < len(b) and a[(i+j) % len(a)] == b[j]:
                j += 1

            if j == len(b):
                return (i+j-1)//len(a)+1
            j = lps[j]
            i += max(1, j - lps[j])
        return -1


